import React, { useState } from 'react';
import '/home/rguktongole/Desktop/ideanexus/frontend/src/components/userprofile/userprofile.css';

const UserProfilePage = () => {
  const defaultProfilePicture =
    '/home/rguktongole/Desktop/ideanexus/frontend/src/assets/profile-placeholder.jpg';
  const [isEditing, setIsEditing] = useState(false);
  const [profilePicture, setProfilePicture] = useState(defaultProfilePicture);
  const [username, setUsername] = useState('JohnDoe');
  const [email, setEmail] = useState('john.doe@example.com');
  const [bio, setBio] = useState('This is my bio');

  const handleEditToggle = () => {
    setIsEditing(!isEditing);
  };

  const handleSave = () => {
    setIsEditing(false); // Exit edit mode after saving
    console.log('Profile saved:', { username, email, bio });
  };

  const handleProfilePictureChange = () => {
    const newPicture = prompt('Enter the URL of your new profile picture:');
    if (newPicture) {
      setProfilePicture(newPicture); // Update profile picture with the new URL
    }
  };

  return (
    <div className="profile-container">
      <div className="profile-header">
        <div className="profile-pic-container">
          <img src={profilePicture} alt="Profile" className="profile-pic" />
          {isEditing && (
            <button
              className="profile-pic-edit-btn"
              onClick={handleProfilePictureChange}
            >
              Change Photo
            </button>
          )}
        </div>

        <h2>User Profile</h2>
        {!isEditing && (
          <button className="edit-btn" onClick={handleEditToggle}>
            Edit Profile
          </button>
        )}
      </div>

      <div className="profile-details">
        {isEditing ? (
          <form className="profile-form">
            <label>Username:</label>
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />

            <label>Email:</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />

            <label>Bio:</label>
            <textarea
              value={bio}
              onChange={(e) => setBio(e.target.value)}
            ></textarea>

            <button type="button" onClick={handleSave} className="save-btn">
              Save Changes
            </button>
          </form>
        ) : (
          <div className="details-view">
            <p>
              <strong>Username:</strong> {username}
            </p>
            <p>
              <strong>Email:</strong> {email}
            </p>
            <p>
              <strong>Bio:</strong> {bio}
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default UserProfilePage;
