import React, { useState, useEffect } from 'react';
import { FaUser, FaEnvelope, FaPencilAlt } from 'react-icons/fa';
import axios from 'axios';
import '/home/rguktongole/Desktop/ideanexus/frontend/src/components/userprofile/userprofile.css';

const UserProfilePage = () => {
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [bio, setBio] = useState('');
    const [profilePic, setProfilePic] = useState(null);
    const [isEditing, setIsEditing] = useState(false);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);

    // Fetch user profile data on component mount
    useEffect(() => {
        const fetchUserProfile = async () => {
            setLoading(true);
            try {
                const token = localStorage.getItem('token');
                if (!token) throw new Error('Unauthorized: Please log in.');

                const response = await axios.get('http://localhost:5002/api/auth/profile', {
                    headers: { Authorization: `Bearer ${token}` },
                });

                const { username, email, bio, profilePic } = response.data;
                setUsername(username);
                setEmail(email);
                setBio(bio || ''); // Default to empty bio
                setProfilePic(profilePic);
            } catch (err) {
                setError(err.response?.data?.message || 'Failed to fetch user profile.');
                console.error('Error fetching profile:', err);
            } finally {
                setLoading(false);
            }
        };

        fetchUserProfile();
    }, []);

    const handleProfilePicChange = (e) => {
        const file = e.target.files[0];
        if (file && file.type.startsWith('image/')) {
            const reader = new FileReader();
            reader.onloadend = () => setProfilePic(reader.result);
            reader.readAsDataURL(file);
        } else {
            setError('Please select a valid image file.');
        }
    };

    const handleEditClick = () => {
        setIsEditing((prev) => !prev);
        setError(''); // Clear errors when toggling edit mode
    };

    const handleSaveChanges = async () => {
        setLoading(true);
        try {
            const token = localStorage.getItem('token');
            if (!token) throw new Error('Unauthorized: Please log in.');

            const response = await axios.put(
                'http://localhost:5002/api/auth/profile',
                { username, email, bio, profilePic },
                { headers: { Authorization: `Bearer ${token}` } }
            );

            console.log('Profile updated successfully:', response.data);
            setError(''); // Clear any previous errors
        } catch (err) {
            setError(err.response?.data?.message || 'Error updating profile. Please try again.');
            console.error('Error updating profile:', err);
        } finally {
            setIsEditing(false);
            setLoading(false);
        }
    };

    return (
        <div className="profile-container">
            {loading && <p className="loading-message">Loading...</p>}
            {error && (
                <div className="error-message">
                    <p>{error}</p>
                    <button onClick={() => setError('')} className="dismiss-error">
                        Dismiss
                    </button>
                </div>
            )}
            <div className="profile-header">
                <div className="profile-pic-container">
                    <img
                        src={profilePic || 'https://via.placeholder.com/150'}
                        alt="Profile"
                        className="profile-pic"
                    />
                    {isEditing && (
                        <input
                            type="file"
                            accept="image/*"
                            onChange={handleProfilePicChange}
                            className="profile-pic-input"
                        />
                    )}
                </div>
                <div className="profile-info">
                    <div className="profile-info-item">
                        <FaUser className="icon" />
                        {isEditing ? (
                            <input
                                type="text"
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                                className="profile-input"
                                placeholder="Enter your username"
                            />
                        ) : (
                            <p className="profile-detail">{username}</p>
                        )}
                    </div>
                    <div className="profile-info-item">
                        <FaEnvelope className="icon" />
                        {isEditing ? (
                            <input
                                type="email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                className="profile-input"
                                placeholder="Enter your email"
                            />
                        ) : (
                            <p className="profile-detail">{email}</p>
                        )}
                    </div>
                    <div className="profile-info-item">
                        <FaPencilAlt className="icon" />
                        {isEditing ? (
                            <textarea
                                value={bio}
                                onChange={(e) => setBio(e.target.value)}
                                className="profile-input"
                                placeholder="Tell us about yourself"
                            />
                        ) : (
                            <p className="profile-detail">{bio || 'No bio available'}</p>
                        )}
                    </div>
                </div>
            </div>
            <div className="profile-actions">
                <button className="edit-button" onClick={handleEditClick} disabled={loading}>
                    {isEditing ? 'Cancel' : 'Edit Profile'}
                </button>
                {isEditing && (
                    <button className="save-button" onClick={handleSaveChanges} disabled={loading}>
                        Save Changes
                    </button>
                )}
            </div>
        </div>
    );
};

export default UserProfilePage;
