import React, { useState } from 'react';
import axios from 'axios';
import '/home/rguktongole/Desktop/ideanexus/frontend/src/styles/signuppage.css';  // Ensure the CSS file is imported

const SignUpPage = () => {
    const [formData, setFormData] = useState({
        email: '',
        password: '',
    });
    const [errorMessage, setErrorMessage] = useState('');
    const [successMessage, setSuccessMessage] = useState('');

    // Handle form input change
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();

        // Basic password validation before submitting the request
        const password = formData.password;

        if (password.length < 8) {
            setErrorMessage('Password must be at least 8 characters long.');
            return;
        }
        if (!/[a-zA-Z]/.test(password)) {
            setErrorMessage('Password must include at least one letter.');
            return;
        }
        if (!/[0-9]/.test(password)) {
            setErrorMessage('Password must include at least one number.');
            return;
        }

        // Clear any previous error messages before making a new request
        setErrorMessage('');
        setSuccessMessage('');

        try {
            // Send signup request to the backend
            const response = await axios.post('http://localhost:5002/signup', formData);

            if (response.data && response.data.message === 'User registered successfully') {
                setSuccessMessage('User registered successfully! You can now log in.');
                setFormData({
                    email: '',
                    password: '',
                });
            }
        } catch (error) {
            // Handle error if the signup fails
            console.error('Sign Up error:', error.response ? error.response.data : error.message);
            setErrorMessage(error.response ? error.response.data.message : 'Error registering user. Please try again.');
        }
    };

    return (
        <div className="signup-container">
            <h2>Sign Up</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleInputChange}
                    placeholder="Enter your email"
                    required
                />
                <input
                    type="password"
                    name="password"
                    value={formData.password}
                    onChange={handleInputChange}
                    placeholder="Enter your password"
                    required
                />
                <button type="submit">Sign Up</button>
            </form>

            {/* Display error or success messages */}
            {errorMessage && <div className="error-message">{errorMessage}</div>}
            {successMessage && <div className="success-message">{successMessage}</div>}
        </div>
    );
};

export default SignUpPage;
