import React, { useState } from 'react';
import axios from 'axios';
import '/home/rguktongole/Desktop/ideanexus/frontend/src/styles/loginpage.css';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate fields
    if (!email || !password) {
      setError('Email and password are required');
      return;
    }

    try {
      const response = await axios.post('http://localhost:5002/api/auth/login', { email, password });

      // Success response
      console.log('Login successful:', response.data);

      // Handle success, e.g., store the token, redirect, etc.
      // localStorage.setItem('token', response.data.token); // Example of saving the token to localStorage
      // window.location.href = "/dashboard"; // Example of redirect

    } catch (err) {
      console.error('Login error:', err);
      if (err.response && err.response.data) {
        setError(err.response.data.message); // Display backend error message
      } else {
        setError('An error occurred during login'); // Generic error message
      }
    }
  };

  return (
    <div className="login-container">
      <form onSubmit={handleSubmit} className="login-form">
        <h2>Login</h2>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
        {error && <p className="error-message">{error}</p>}
      </form>
    </div>
  );
};

export default LoginPage;
