import React from 'react';
import { FaSearch, FaUser, FaEnvelope } from 'react-icons/fa'; // Importing icons from react-icons
import '/home/rguktongole/Desktop/ideanexus/frontend/src/components/dashboard/UserDashboard.css'; // Use a relative path for consistency
import { useNavigate } from 'react-router-dom'; // Importing useNavigate for navigation
import './UserDashboard.css'; // Use relative path for consistency

const Dashboard = ({ messageCount }) => {
    const navigate = useNavigate();

    return (
        <div className="dashboard-container">
            <header className="dashboard-header">
                {/* Profile Icon */}
                <div
                    className="profile-logo"
                    title="Profile"
                    onClick={() => navigate('/profile')} // Navigate to User Profile Page
                >
                    <FaUser />
                </div>

                {/* Search Bar */}
                <div className="search-container">
                    <input
                        type="text"
                        className="search-bar"
                        placeholder="Search..."
                        aria-label="Search"
                    />
                    <FaSearch className="search-icon" />
                </div>

                {/* Message Icon */}
                <div
                    className="message-icon"
                    title="Messages"
                    onClick={() => navigate('/chats')} // Navigate to Chats Page
                >
                    <FaEnvelope />
                    {messageCount > 0 && (
                        <span className="message-badge">{messageCount}</span> // Display message count if new messages exist
                    )}
                </div>
            </header>

            <main className="dashboard-content">
                {/* Example Content */}
                <h1>Welcome to IdeaNexus!</h1>
                <p>Your posts, profiles, and interactions will be displayed here. Start exploring!</p>
            </main>
        </div>
    );
};

export default Dashboard;
