import React, { useState, useEffect } from 'react';
import { FaUser, FaEnvelope, FaPencilAlt } from 'react-icons/fa';
import axios from 'axios';
import '/home/rguktongole/Desktop/ideanexus/frontend/src/components/userprofile/userprofile.css'; // Ensure the correct path for the CSS file

const UserProfilePage = () => {
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [bio, setBio] = useState('');
    const [profilePic, setProfilePic] = useState(null);
    const [isEditing, setIsEditing] = useState(false);
    const [error, setError] = useState('');

    useEffect(() => {
        // Fetch user profile data
        const fetchUserProfile = async () => {
            try {
                const token = localStorage.getItem('token');
                if (token) {
                    const response = await axios.get('http://localhost:5002/api/auth/profile', {
                        headers: { Authorization: `Bearer ${token}` }
                    });
                    const { username, email, bio, profilePic } = response.data;
                    setUsername(username);
                    setEmail(email);
                    setBio(bio);
                    setProfilePic(profilePic);
                }
            } catch (err) {
                setError('Failed to fetch user profile.');
                console.error('Error fetching profile:', err);
            }
        };

        fetchUserProfile();
    }, []);

    const handleProfilePicChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onloadend = () => {
                setProfilePic(reader.result);
            };
            reader.readAsDataURL(file);
        }
    };

    const handleEditClick = () => {
        setIsEditing(!isEditing);
    };

    const handleSaveChanges = async () => {
        try {
            const token = localStorage.getItem('token');
            if (token) {
                const response = await axios.put('http://localhost:5002/api/auth/profile', {
                    username,
                    email,
                    bio,
                    profilePic
                }, {
                    headers: { Authorization: `Bearer ${token}` }
                });

                // Optionally show a success message or notification
                console.log('Profile updated successfully:', response.data);
            }
        } catch (err) {
            setError('Error updating profile. Please try again.');
            console.error('Error updating profile:', err);
        }
        setIsEditing(false);
    };

    return (
        <div className="profile-container">
            <div className="profile-header">
                <div className="profile-pic-container">
                    <img 
                        src={profilePic || 'https://via.placeholder.com/150'} 
                        alt="Profile" 
                        className="profile-pic" 
                    />
                    <input 
                        type="file" 
                        accept="image/*" 
                        onChange={handleProfilePicChange} 
                        className="profile-pic-input" 
                    />
                </div>
                <div className="profile-info">
                    <div className="profile-info-item">
                        <FaUser className="icon" />
                        {isEditing ? (
                            <input 
                                type="text" 
                                value={username} 
                                onChange={(e) => setUsername(e.target.value)} 
                                className="profile-input" 
                                placeholder="Enter your username"
                            />
                        ) : (
                            <p className="profile-detail">{username}</p>
                        )}
                    </div>
                    <div className="profile-info-item">
                        <FaEnvelope className="icon" />
                        {isEditing ? (
                            <input 
                                type="email" 
                                value={email} 
                                onChange={(e) => setEmail(e.target.value)} 
                                className="profile-input" 
                                placeholder="Enter your email"
                            />
                        ) : (
                            <p className="profile-detail">{email}</p>
                        )}
                    </div>
                    <div className="profile-info-item">
                        <FaPencilAlt className="icon" />
                        {isEditing ? (
                            <textarea 
                                value={bio} 
                                onChange={(e) => setBio(e.target.value)} 
                                className="profile-input" 
                                placeholder="Tell us about yourself"
                            />
                        ) : (
                            <p className="profile-detail">{bio}</p>
                        )}
                    </div>
                </div>
            </div>
            <div className="profile-actions">
                <button className="edit-button" onClick={handleEditClick}>
                    {isEditing ? 'Cancel' : 'Edit Profile'}
                </button>
                {isEditing && (
                    <button className="save-button" onClick={handleSaveChanges}>
                        Save Changes
                    </button>
                )}
            </div>
            {error && <p className="error-message">{error}</p>}
        </div>
    );
};

export default UserProfilePage;
