import React, { useEffect, useState } from 'react';
import { fetchUserDetails, updateUserDetails } from '/home/rguktongole/Desktop/ideanexus/frontend/src/services/userservice.js';
import '/home/rguktongole/Desktop/ideanexus/frontend/src/styles/profilepage.css';

const ProfilePage = ({ userId }) => {
  const [userDetails, setUserDetails] = useState({ name: '', email: '', bio: '' });
  const [editMode, setEditMode] = useState(false);
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const data = await fetchUserDetails(userId);
        setUserDetails(data);
      } catch (err) {
        setError(err.message || 'Failed to fetch user details.');
      }
    };

    if (userId) fetchData();
    else setError('User ID is missing. Please log in again.');
  }, [userId]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserDetails((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const updatedData = await updateUserDetails({ ...userDetails });
      setUserDetails(updatedData.user);
      setSuccessMessage(updatedData.message);
      setEditMode(false);
    } catch (err) {
      setError(err.message || 'Failed to update profile.');
    }
  };

  return (
    <div className="profile-container">
      <h1>User Profile</h1>
      {error && <p className="error-message">{error}</p>}
      {successMessage && <p className="success-message">{successMessage}</p>}

      {!editMode ? (
        <div>
          <p><strong>Name:</strong> {userDetails.name || 'N/A'}</p>
          <p><strong>Email:</strong> {userDetails.email || 'N/A'}</p>
          <p><strong>Bio:</strong> {userDetails.bio || 'N/A'}</p>
          <button onClick={() => setEditMode(true)}>Edit Profile</button>
        </div>
      ) : (
        <form onSubmit={handleSubmit}>
          <input name="name" value={userDetails.name} onChange={handleInputChange} placeholder="Name" required />
          <input name="email" type="email" value={userDetails.email} onChange={handleInputChange} placeholder="Email" required />
          <textarea name="bio" value={userDetails.bio} onChange={handleInputChange} placeholder="Bio" rows="4"></textarea>
          <button type="submit">Save</button>
          <button type="button" onClick={() => setEditMode(false)}>Cancel</button>
        </form>
      )}
    </div>
  );
};

export default ProfilePage;
