import React, { useState } from 'react';
import '/home/rguktongole/Desktop/ideanexus/frontend/src/components/userprofile/userprofile.css';

const UserProfilePage = () => {
  const defaultProfilePicture =
    '/home/rguktongole/Desktop/ideanexus/frontend/src/assets/profile-placeholder.jpg';
  const [username, setUsername] = useState('JohnDoe');
  const [email, setEmail] = useState('john.doe@example.com');
  const [bio, setBio] = useState('This is my bio');
  const [profilePicture, setProfilePicture] = useState(defaultProfilePicture);

  const handleSave = () => {
    // Save profile changes (for now, just log to console)
    console.log('Profile saved:', { username, email, bio });
  };

  const handleProfilePictureChange = (e) => {
    if (e.target.files[0]) {
      setProfilePicture(URL.createObjectURL(e.target.files[0])); // Update profile picture
    }
  };

  return (
    <div className="profile-container">
      <div className="profile-header">
        <div className="profile-pic-container">
          <img
            src={profilePicture}
            alt="Profile"
            className="profile-pic"
          />
          <input
            type="file"
            accept="image/*"
            onChange={handleProfilePictureChange}
            className="profile-pic-upload"
          />
        </div>
        <h2>Edit Your Profile</h2>
      </div>

      <form className="profile-form">
        <label>Username:</label>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />

        <label>Email:</label>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />

        <label>Bio:</label>
        <textarea
          value={bio}
          onChange={(e) => setBio(e.target.value)}
        />

        <button type="button" onClick={handleSave} className="save-btn">
          Save Changes
        </button>
      </form>
    </div>
  );
};

export default UserProfilePage;
