import React, { useState } from 'react';
import '/home/rguktongole/Desktop/ideanexus/frontend/src/components/userprofile/userprofile.css';

const UserProfilePage = () => {
  const [username, setUsername] = useState('JohnDoe');
  const [email, setEmail] = useState('john.doe@example.com');
  const [bio, setBio] = useState('This is my bio.');
  const [profilePicture, setProfilePicture] = useState('https://via.placeholder.com/150'); // Default profile picture

  const handleProfilePictureChange = (e) => {
    if (e.target.files[0]) {
      setProfilePicture(URL.createObjectURL(e.target.files[0])); // Preview selected image
    }
  };

  const handleSave = () => {
    alert('Profile updated successfully!');
    console.log({ username, email, bio });
  };

  return (
    <div className="profile-container">
      <h2>Edit Your Profile</h2>
      <div className="profile-header">
        {/* Profile Picture */}
        <div className="profile-pic-container">
          <img src={profilePicture} alt="Profile" className="profile-pic" />
          <input
            type="file"
            accept="image/*"
            onChange={handleProfilePictureChange}
            className="profile-pic-upload"
          />
        </div>
      </div>

      {/* Profile Form */}
      <form className="profile-form">
        <label>Username:</label>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />

        <label>Email:</label>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />

        <label>Bio:</label>
        <textarea
          value={bio}
          onChange={(e) => setBio(e.target.value)}
        ></textarea>

        <button type="button" onClick={handleSave} className="save-btn">
          Save Changes
        </button>
      </form>
    </div>
  );
};

export default UserProfilePage;
