import React, { useState } from 'react';
import { FaUser, FaEnvelope, FaPencilAlt } from 'react-icons/fa';
import ''; // Ensure the correct path for the CSS file

const UserProfilePage = () => {
    const [username, setUsername] = useState('John Doe');
    const [email, setEmail] = useState('johndoe@example.com');
    const [bio, setBio] = useState('This is my bio.');
    const [profilePic, setProfilePic] = useState(null);
    const [isEditing, setIsEditing] = useState(false);

    const handleProfilePicChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onloadend = () => {
                setProfilePic(reader.result);
            };
            reader.readAsDataURL(file);
        }
    };

    const handleEditClick = () => {
        setIsEditing(!isEditing);
    };

    const handleSaveChanges = () => {
        setIsEditing(false);
        // Here, you can add logic to save the updated profile to a backend or state
    };

    return (
        <div className="profile-container">
            <div className="profile-header">
                <div className="profile-pic-container">
                    <img 
                        src={profilePic || 'https://via.placeholder.com/150'} 
                        alt="Profile" 
                        className="profile-pic" 
                    />
                    <input 
                        type="file" 
                        accept="image/*" 
                        onChange={handleProfilePicChange} 
                        className="profile-pic-input" 
                    />
                </div>
                <div className="profile-info">
                    <div className="profile-info-item">
                        <FaUser className="icon" />
                        {isEditing ? (
                            <input 
                                type="text" 
                                value={username} 
                                onChange={(e) => setUsername(e.target.value)} 
                                className="profile-input" 
                                placeholder="Enter your username"
                            />
                        ) : (
                            <p className="profile-detail">{username}</p>
                        )}
                    </div>
                    <div className="profile-info-item">
                        <FaEnvelope className="icon" />
                        {isEditing ? (
                            <input 
                                type="email" 
                                value={email} 
                                onChange={(e) => setEmail(e.target.value)} 
                                className="profile-input" 
                                placeholder="Enter your email"
                            />
                        ) : (
                            <p className="profile-detail">{email}</p>
                        )}
                    </div>
                    <div className="profile-info-item">
                        <FaPencilAlt className="icon" />
                        {isEditing ? (
                            <textarea 
                                value={bio} 
                                onChange={(e) => setBio(e.target.value)} 
                                className="profile-input" 
                                placeholder="Tell us about yourself"
                            />
                        ) : (
                            <p className="profile-detail">{bio}</p>
                        )}
                    </div>
                </div>
            </div>
            <div className="profile-actions">
                <button className="edit-button" onClick={handleEditClick}>
                    {isEditing ? 'Cancel' : 'Edit Profile'}
                </button>
                {isEditing && (
                    <button className="save-button" onClick={handleSaveChanges}>
                        Save Changes
                    </button>
                )}
            </div>
        </div>
    );
};

export default UserProfilePage;
