import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate
import axios from 'axios';
import '/home/rguktongole/Desktop/ideanexus/frontend/src/styles/signuppage.css'; // Ensure correct path

const SignUpPage = () => {
  const [name, setName] = useState('');
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [userType, setUserType] = useState('');
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  const navigate = useNavigate(); // Initialize the navigate function

  // Updated password validation function that allows special characters
  const validatePassword = (password) => {
    if (password.length < 8) {
      return 'Password must be at least 8 characters long.';
    }
    if (!/[a-zA-Z]/.test(password)) {
      return 'Password must include at least one letter.';
    }
    if (!/[0-9]/.test(password)) {
      return 'Password must include at least one number.';
    }
    // Special characters are allowed, no additional validation needed
    return '';
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate the password
    const passwordError = validatePassword(password);
    if (passwordError) {
      setError(passwordError);
      return;
    }

    // Clear previous error and success messages
    setError('');
    setSuccessMessage('');

    try {
      // Send the sign-up request
      const response = await axios.post('http://localhost:5002/api/auth/signup', {
        name,
        username,
        email,
        password,
        userType,
      });

      // Handle successful sign-up
      console.log('Sign Up successful:', response.data);
      setSuccessMessage('User registered successfully!');
      setName('');
      setUsername('');
      setEmail('');
      setPassword('');
      setUserType('');

      // Redirect to the login page after successful sign-up (Optional step)
      navigate('/login');
    } catch (err) {
      // Handle errors from the backend
      console.error('Sign Up error:', err);
      setError(err.response ? err.response.data.message : 'Error registering user. Please try again.');
      setSuccessMessage('');
    }
  };

  return (
    <div className="signin-container">
      <div className="signin-box">
        <h2>Sign Up</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <input
              type="text"
              placeholder="Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <select
              value={userType}
              onChange={(e) => setUserType(e.target.value)}
              required
            >
              <option value="">Select User Type</option>
              <option value="student">Student</option>
              <option value="professional">Professional</option>
            </select>
          </div>
          <button type="submit" className="btn-signin">Sign Up</button>
          {error && <p className="error-message">{error}</p>}
          {successMessage && <p className="success-message">{successMessage}</p>}
        </form>
      </div>
    </div>
  );
};

export default SignUpPage;
