import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom"; // Use for navigation
import ""; // Import CSS

const ProfilePage = () => {
  const [userDetails, setUserDetails] = useState({
    userId: "",
    username: "",
    email: "",
    bio: "",
  });
  const [message, setMessage] = useState(""); // Success/Error messages
  const navigate = useNavigate(); // React Router hook for navigation

  useEffect(() => {
    // Fetch user details on component mount
    const fetchUserDetails = async () => {
      const userId = localStorage.getItem("userId"); // Retrieve userId from localStorage
      if (!userId) {
        console.error("User ID is missing");
        setMessage("User ID is missing. Please log in again.");
        setTimeout(() => navigate("/login"), 3000); // Redirect to login page after 3 seconds
        return;
      }

      try {
        const response = await axios.get(`http://localhost:5002/api/user/${userId}`);
        setUserDetails({ ...response.data, userId });
      } catch (error) {
        console.error("Failed to fetch user details:", error.message);
        setMessage("Failed to fetch user details. Please try again later.");
      }
    };

    fetchUserDetails();
  }, [navigate]);

  const handleUpdateProfile = async () => {
    const { userId, username, email, bio } = userDetails;

    if (!userId) {
      console.error("User ID is missing");
      setMessage("User ID is missing. Unable to update profile.");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5002/api/user/update", {
        userId,
        username,
        email,
        bio,
      });
      setMessage(response.data.message); // Display success message
    } catch (error) {
      setMessage(error.response?.data?.message || "Failed to update profile"); // Display error message
    }
  };

  return (
    <div className="profile-page">
      <h1>Profile Page</h1>
      {message && (
        <p className={`message ${message.includes("successfully") ? "success" : "error"}`}>
          {message}
        </p>
      )}
      <input
        type="text"
        value={userDetails.username}
        onChange={(e) => setUserDetails({ ...userDetails, username: e.target.value })}
        placeholder="Username"
      />
      <input
        type="email"
        value={userDetails.email}
        onChange={(e) => setUserDetails({ ...userDetails, email: e.target.value })}
        placeholder="Email"
      />
      <textarea
        value={userDetails.bio}
        onChange={(e) => setUserDetails({ ...userDetails, bio: e.target.value })}
        placeholder="Write your bio here..."
        rows="4"
        cols="50"
      />
      <button onClick={handleUpdateProfile}>Update Profile</button>
    </div>
  );
};

export default ProfilePage;
