import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';

// Importing components
import WelcomePage from '/home/rguktongole/Desktop/ideanexus/frontend/src/components/welcomepage.js';
import LoginPage from './components/loginpage.js';
import SignUpPage from './components/signuppage.js';
import ProfilePage from './components/profilepage.js';
import Dashboard from './components/dashboard/userdashboard.js'; // Dashboard page
import UserProfilePage from './components/userprofile/userprofile.js'; // UserProfilePage

const App = () => {
  return (
    <Router>
      <div className="App">
        {/* Header with Profile Icon */}
        <header className="App-header" style={headerStyle}>
          <Link to="/user-profile" style={{ textDecoration: 'none' }}>
            <div className="profile-icon" style={iconStyle}>
              <i className="fa fa-user" style={iconInnerStyle}></i>
            </div>
          </Link>
        </header>

        {/* Routes */}
        <Routes>
          <Route path="/" element={<WelcomePage />} />
          <Route path="/login" element={<LoginPage />} />
          <Route path="/signup" element={<SignUpPage />} />
          <Route path="/profile" element={<ProfilePage />} />
          <Route path="/dashboard" element={<Dashboard />} />
          <Route path="/user-profile" element={<UserProfilePage />} />
        </Routes>
      </div>
    </Router>
  );
};

/* Inline styles for simplicity */
const headerStyle = {
  display: 'flex',
  justifyContent: 'flex-end', // Align the profile icon to the right
  padding: '10px 20px',
  backgroundColor: '#f8f9fa',
  borderBottom: '1px solid #ddd',
};

const iconStyle = {
  cursor: 'pointer',
  padding: '5px',
  borderRadius: '50%',
  backgroundColor: '#007bff', // Blue background for the icon
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  width: '40px',
  height: '40px',
};

const iconInnerStyle = {
  color: '#fff', // White icon color
  fontSize: '18px',
};

export default App;
