import React, { useState } from 'react';
import { updateUserDetails } from '/home/rguktongole/Desktop/ideanexus/frontend/src/services/userservice.js';
import '/home/rguktongole/Desktop/ideanexus/frontend/src/styles/profilepage.css';

const ProfilePage = () => {
  const [userDetails, setUserDetails] = useState({ name: '', email: '', bio: '' });
  const [editMode, setEditMode] = useState(false);
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserDetails((prevDetails) => ({ ...prevDetails, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await updateUserDetails(userDetails);
      setEditMode(false);
      setSuccessMessage('Profile updated successfully!');
      setError('');
    } catch (err) {
      if (err.message === 'Authentication token is missing. Please log in again.') {
        setError('Your session has expired. Please log in again.');
        localStorage.removeItem('token');
      } else {
        setError(err.message || 'Failed to update profile. Please try again.');
      }
      setSuccessMessage('');
    }
  };

  return (
    <div className="profile-container">
      <h1>User Profile</h1>
      {error && <p className="error-message fade-in">{error}</p>}
      {successMessage && <p className="success-message fade-in">{successMessage}</p>}

      {!editMode ? (
        <div className="profile-details">
          <p><strong>Name:</strong> {userDetails.name || 'N/A'}</p>
          <p><strong>Email:</strong> {userDetails.email || 'N/A'}</p>
          <p><strong>Bio:</strong> {userDetails.bio || 'No bio available'}</p>
          <button className="edit-button" onClick={() => setEditMode(true)}>Edit Profile</button>
        </div>
      ) : (
        <form onSubmit={handleSubmit} className="profile-form">
          <input
            type="text"
            name="name"
            value={userDetails.name || ''}
            onChange={handleInputChange}
            placeholder="Name"
            required
          />
          <input
            type="email"
            name="email"
            value={userDetails.email || ''}
            onChange={handleInputChange}
            placeholder="Email"
            required
          />
          <textarea
            name="bio"
            value={userDetails.bio || ''}
            onChange={handleInputChange}
            placeholder="Bio"
            rows="4"
          />
          <div className="form-buttons">
            <button type="submit" className="save-button">Save</button>
            <button type="button" className="cancel-button" onClick={() => setEditMode(false)}>Cancel</button>
          </div>
        </form>
      )}
    </div>
  );
};

export default ProfilePage;
