import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';

// Importing components
import WelcomePage from '/home/rguktongole/Desktop/ideanexus/frontend/src/components/welcomepage.js';
import LoginPage from '/home/rguktongole/Desktop/ideanexus/frontend/src/components/loginpage.js';
import SignUpPage from '/home/rguktongole/Desktop/ideanexus/frontend/src/components/signuppage.js';
import Dashboard from '/home/rguktongole/Desktop/ideanexus/frontend/src/components/dashboard/userdashboard.js';
import UserProfilePage from '/home/rguktongole/Desktop/ideanexus/frontend/src/components/userprofile/userprofile.js';

const AppHeader = () => {
  const location = useLocation();
  const excludedRoutes = ['/', '/login', '/signup']; // No header on these routes

  if (excludedRoutes.includes(location.pathname)) {
    return null;
  }

  const headerStyle = {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
    padding: '10px 20px',
    backgroundColor: '#f8f9fa',
    borderBottom: '1px solid #ddd',
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    zIndex: 1000,
    height: '60px',
  };

  const iconStyle = {
    cursor: 'pointer',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    fontSize: '20px',
    color: '#007bff',
    marginRight: '10px',
  };

  const searchBarStyle = {
    flex: '1',
    maxWidth: '65%',
    margin: '0 10px',
    display: 'flex',
    alignItems: 'center',
    backgroundColor: '#fff',
    border: '1px solid #ddd',
    borderRadius: '4px',
    padding: '5px 10px',
  };

  return (
    <header style={headerStyle}>
      <Link to="/user-profile" style={{ textDecoration: 'none' }}>
        <div style={iconStyle}>
          <i className="fa fa-user"></i>
        </div>
      </Link>
      <div style={searchBarStyle}>
        <i className="fa fa-search" style={{ marginRight: '5px', color: '#666' }}></i>
        <input
          type="text"
          placeholder="Search..."
          style={{
            border: 'none',
            outline: 'none',
            width: '100%',
            fontSize: '14px',
          }}
        />
      </div>
      <Link to="/dashboard" style={{ textDecoration: 'none' }}>
        <div style={iconStyle}>
          <i className="fa fa-envelope"></i>
        </div>
      </Link>
    </header>
  );
};

const App = () => {
  return (
    <Router>
      <div className="App">
        <AppHeader />
        <Routes>
          <Route path="/" element={<WelcomePage />} />
          <Route path="/login" element={<LoginPage />} />
          <Route path="/signup" element={<SignUpPage />} />
          <Route path="/dashboard" element={<Dashboard />} />
          <Route path="/user-profile" element={<UserProfilePage />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
