import axios from 'axios';

export const fetchUserDetails = async (userId) => {
    console.log('Fetching user details for userId:', userId); // Debugging log
    try {
        const response = await axios.get(`http://localhost:3001/api/user/${userId}`);
        return response.data;
    } catch (error) {
        console.error('Error in fetchUserDetails:', error);
        throw error;
    }
};

export const updateUserDetails = async (userId, userDetails) => {
    console.log('Updating user details with:', { userId, userDetails }); // Debugging log
    if (!userId) {
        console.error('User ID is missing in updateUserDetails.');
        throw new Error('User ID is missing.');
    }

    try {
        const response = await axios.put('http://localhost:3001/api/updateProfile', {
            userId,
            ...userDetails,
        });
        return response.data.user;
    } catch (error) {
        console.error('Error in updateUserDetails:', error);
        throw error;
    }
};
