import React, { useState } from 'react';
import axios from 'axios';
import '.'; // Adjust the path if necessary


const SignUpPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Make a POST request to the backend for user signup
      const response = await axios.post('http://localhost:5002/api/auth/signup', { email, password });

      if (response.status === 201) {
        // On successful signup
        setSuccessMessage('User registered successfully. You can now log in!');
        setError('');
      }
    } catch (err) {
      // On error
      console.error('Signup error:', err);
      setError('An error occurred during sign-up. Please try again.');
      setSuccessMessage('');
    }
  };

  return (
    <div>
      <h2>Sign Up</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Email</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Sign Up</button>
      </form>

      {/* Show success or error message */}
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {successMessage && <p style={{ color: 'green' }}>{successMessage}</p>}
    </div>
  );
};

export default SignUpPage;
