import React, { useEffect, useState } from "react";
import "/home/rguktongole/Desktop/ideanexus/frontend/src/styles/UserDashboard.css"; // Importing styles for the dashboard
import axios from "axios";

const UserDashboard = () => {
  const [posts, setPosts] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  // Fetch posts and user profiles (mock data)
  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get("http://localhost:5002/api/posts"); // Adjust this route based on your backend
        setPosts(response.data);
      } catch (error) {
        console.error("Error fetching posts:", error);
      }
    };

    fetchPosts();
  }, []);

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  return (
    <div className="dashboard-container">
      {/* Header Section */}
      <header className="dashboard-header">
        <div className="profile-logo">
          <img
            src="/home/rguktongole/Desktop/ideanexus/frontend/src/assets/profile-placeholder.jpg"
            alt="Profile"
            className="profile-img"
          />
        </div>
        <div className="search-bar">
          <input
            type="text"
            placeholder="Search..."
            value={searchTerm}
            onChange={handleSearch}
          />
        </div>
        <div className="messages-icon">
          <img
            src="/home/rguktongole/Desktop/ideanexus/frontend/src/assets/message -icon.jpg"
            alt="Messages"
            className="messages-img"
          />
        </div>
      </header>

      {/* Main Content */}
      <main className="dashboard-main">
        <h2>Explore Posts</h2>
        <div className="posts-container">
          {posts
            .filter((post) =>
              post.content.toLowerCase().includes(searchTerm.toLowerCase())
            )
            .map((post) => (
              <div key={post.id} className="post-card">
                <h3>{post.title}</h3>
                <p>{post.content}</p>
                <span className="post-author">By: {post.author}</span>
              </div>
            ))}
        </div>
      </main>
    </div>
  );
};

export default UserDashboard;
