import React, { useState, useEffect } from 'react';
import { fetchUserProfile, updateUserProfile } from '/home/rguktongole/Desktop/ideanexus/frontend/src/utils/api.js'; // Import API functions
import { FaUser, FaEnvelope, FaPencilAlt } from 'react-icons/fa';
import axios from 'axios';
import '/home/rguktongole/Desktop/ideanexus/frontend/src/components/dashboard/UserDashboard.css';

const UserProfilePage = () => {
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [bio, setBio] = useState('');
    const [profilePic, setProfilePic] = useState(null);
    const [isEditing, setIsEditing] = useState(false);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);

    const defaultProfilePic = 'https://via.placeholder.com/150'; // Default profile picture

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            fetchUserProfile(token)
                .then(data => {
                    const { username, email, bio, profilePic } = data;
                    setUsername(username);
                    setEmail(email);
                    setBio(bio || '');
                    setProfilePic(profilePic || defaultProfilePic);
                })
                .catch(err => setError(err));
        } else {
            setError('Unauthorized: Please log in.');
        }
    }, []);

    const handleProfilePicChange = (e) => {
        const file = e.target.files[0];
        if (file && file.type.startsWith('image/')) {
            const reader = new FileReader();
            reader.onloadend = () => setProfilePic(reader.result);
            reader.readAsDataURL(file);
        } else {
            setError('Please select a valid image file.');
        }
    };

    const handleEditClick = () => {
        setIsEditing(!isEditing);
        setError(''); // Clear errors
    };

    const handleSaveChanges = async () => {
        setLoading(true);
        try {
            const token = localStorage.getItem('token');
            if (!token) throw new Error('Unauthorized: Please log in.');

            await updateUserProfile(token, { username, email, bio, profilePic });
            setError('');
            setIsEditing(false);
        } catch (err) {
            setError(err.response?.data?.message || 'Error updating profile. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="profile-container">
            {loading && <p>Loading...</p>}
            {error && <p className="error">{error}</p>}
            {/* Profile information and editing form here */}
        </div>
    );
};

export default UserProfilePage;
