import React, { useState } from 'react';
import axios from 'axios';
import '/home/rguktongole/Desktop/ideanexus/frontend/src/styles/signuppage.css';

const SignupPage = () => {
  const [formData, setFormData] = useState({
    name: '',   // Added name field
    username: '',
    email: '',
    password: '',
    userType: 'student',
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
    setError(''); // Clear errors when user updates input
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    try {
      const response = await axios.post('http://localhost:5002/api/auth/signup', formData, {
        headers: {
          'Content-Type': 'application/json',
        },
      });
      console.log('Signup successful:', response.data);
      setSuccess('Signup successful! You can now log in.');
      setFormData({
        name: '',   // Reset name field
        username: '',
        email: '',
        password: '',
        userType: 'student',
      });
    } catch (err) {
      console.error('Signup error:', err);
      if (err.response && err.response.data && err.response.data.message) {
        setError(err.response.data.message); // Use backend-provided error message
      } else {
        setError('An error occurred during signup. Please try again.');
      }
    }
  };

  return (
    <div className="signup-container">
      <form onSubmit={handleSubmit} className="signup-form">
        <h2>Signup</h2>
        <input
          type="text"
          name="name"   // Added name input
          placeholder="Full Name"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="username"
          placeholder="Username"
          value={formData.username}
          onChange={handleChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={formData.password}
          onChange={handleChange}
          required
        />
        <select name="userType" value={formData.userType} onChange={handleChange}>
          <option value="student">Student</option>
          <option value="professional">Graduated/Professional</option>
        </select>
        <button type="submit">Signup</button>
        {error && <p className="error-message">{error}</p>}
        {success && <p className="success-message">{success}</p>}
      </form>
    </div>
  );
};

export default SignupPage;
