import React, { useEffect, useState } from 'react';
import { fetchUserDetails, updateUserDetails } from '/home/rguktongole/Desktop/ideanexus/frontend/src/services/userservice.js';
import '/home/rguktongole/Desktop/ideanexus/frontend/src/styles/profilepage.css';

const ProfilePage = ({ userId }) => {
    const [userDetails, setUserDetails] = useState({});
    const [editMode, setEditMode] = useState(false);

    useEffect(() => {
        const fetchData = async () => {
            const data = await fetchUserDetails(userId);
            setUserDetails(data);
        };
        fetchData();
    }, [userId]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setUserDetails({ ...userDetails, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const updatedUser = await updateUserDetails(userId, userDetails);
        setUserDetails(updatedUser);
        setEditMode(false);
    };

    return (
        <div className="profile-container">
            <h1>User Profile</h1>
            {!editMode ? (
                <div className="profile-details">
                    <p><strong>Name:</strong> {userDetails.name}</p>
                    <p><strong>Email:</strong> {userDetails.email}</p>
                    <p><strong>Bio:</strong> {userDetails.bio || 'No bio available'}</p>
                    <button onClick={() => setEditMode(true)}>Edit Profile</button>
                </div>
            ) : (
                <form onSubmit={handleSubmit} className="profile-form">
                    <input
                        type="text"
                        name="name"
                        value={userDetails.name || ''}
                        onChange={handleInputChange}
                        placeholder="Name"
                    />
                    <input
                        type="email"
                        name="email"
                        value={userDetails.email || ''}
                        onChange={handleInputChange}
                        placeholder="Email"
                    />
                    <textarea
                        name="bio"
                        value={userDetails.bio || ''}
                        onChange={handleInputChange}
                        placeholder="Bio"
                    />
                    <button type="submit">Save</button>
                    <button type="button" onClick={() => setEditMode(false)}>Cancel</button>
                </form>
            )}
        </div>
    );
};

export default ProfilePage;
