import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '/home/rguktongole/Desktop/ideanexus/frontend/src/components/userprofile/userprofile.css';
import DefaultProfilePicture from '/home/rguktongole/Desktop/ideanexus/frontend/src/assets/profile-placeholder.jpg';

const UserProfilePage = () => {
  const [isEditing, setIsEditing] = useState(false);
  const [profileData, setProfileData] = useState({
    username: '',
    email: '',
    bio: '',
    profilePicture: DefaultProfilePicture,
  });

  useEffect(() => {
    // Fetch user profile on load
    axios.get('http://localhost:5002/api/user/profile')
      .then((response) => {
        setProfileData({
          ...response.data,
          profilePicture: response.data.profilePicture || DefaultProfilePicture,
        });
      })
      .catch((error) => {
        console.error('Error fetching profile:', error);
      });
  }, []);

  const handleEditToggle = () => setIsEditing(!isEditing);

  const handleSave = () => {
    axios.put('http://localhost:5002/api/user/profile', profileData)
      .then((response) => {
        setProfileData(response.data.user);
        setIsEditing(false);
      })
      .catch((error) => {
        console.error('Error updating profile:', error);
      });
  };

  const handleProfilePictureChange = (e) => {
    if (e.target.files[0]) {
      const file = e.target.files[0];
      const imageURL = URL.createObjectURL(file);
      setProfileData((prev) => ({ ...prev, profilePicture: imageURL }));
    }
  };

  return (
    <div className="profile-container">
      <div className="profile-header">
        <div className="profile-pic-container">
          <img src={profileData.profilePicture} alt="Profile" className="profile-pic" />
          {isEditing && (
            <div className="change-photo-section">
              <label className="custom-file-upload">
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleProfilePictureChange}
                  style={{ display: 'none' }}
                />
                Change Photo
              </label>
            </div>
          )}
        </div>

        {!isEditing && <h2>{profileData.username}</h2>}
        {!isEditing && (
          <button className="edit-btn" onClick={handleEditToggle}>
            Edit Profile
          </button>
        )}
      </div>

      <div className="profile-details">
        {isEditing ? (
          <form className="profile-form">
            <label>Username:</label>
            <input
              type="text"
              value={profileData.username}
              onChange={(e) =>
                setProfileData((prev) => ({ ...prev, username: e.target.value }))
              }
            />

            <label>Email:</label>
            <input
              type="email"
              value={profileData.email}
              onChange={(e) =>
                setProfileData((prev) => ({ ...prev, email: e.target.value }))
              }
            />

            <label>Bio:</label>
            <textarea
              value={profileData.bio}
              onChange={(e) =>
                setProfileData((prev) => ({ ...prev, bio: e.target.value }))
              }
            ></textarea>

            <button type="button" onClick={handleSave} className="save-btn">
              Save Changes
            </button>
          </form>
        ) : (
          <div className="details-view">
            <p>
              <strong>Email:</strong> {profileData.email}
            </p>
            <p>
              <strong>Bio:</strong> {profileData.bio}
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default UserProfilePage;
