import React, { useState } from 'react';
import { FaUser, FaEnvelope } from 'react-icons/fa';
import '/home/rguktongole/Desktop/ideanexus/frontend/src/components/userprofile/userprofile.css'; // Ensure correct path for CSS

const UserProfilePage = () => {
    const [username, setUsername] = useState('John Doe'); // Example user info
    const [email, setEmail] = useState('johndoe@example.com');
    const [profilePic, setProfilePic] = useState(null);
    const [isEditing, setIsEditing] = useState(false);

    const handleProfilePicChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onloadend = () => {
                setProfilePic(reader.result);
            };
            reader.readAsDataURL(file);
        }
    };

    const handleEditClick = () => {
        setIsEditing(!isEditing); // Toggle editing state
    };

    const handleSaveChanges = () => {
        setIsEditing(false); // Save changes and stop editing
        // Add logic to save the changes (e.g., call API to update profile info)
    };

    return (
        <div className="profile-container">
            <div className="profile-header">
                <div className="profile-pic-container">
                    <img 
                        src={profilePic || 'https://via.placeholder.com/150'} 
                        alt="Profile" 
                        className="profile-pic" 
                    />
                    <input 
                        type="file" 
                        accept="image/*" 
                        onChange={handleProfilePicChange} 
                        className="profile-pic-input" 
                    />
                </div>
                <div className="profile-info">
                    <div className="profile-info-item">
                        <FaUser className="icon" />
                        {isEditing ? (
                            <input 
                                type="text" 
                                value={username} 
                                onChange={(e) => setUsername(e.target.value)} 
                                className="profile-input" 
                            />
                        ) : (
                            <p className="profile-detail">{username}</p>
                        )}
                    </div>
                    <div className="profile-info-item">
                        <FaEnvelope className="icon" />
                        {isEditing ? (
                            <input 
                                type="email" 
                                value={email} 
                                onChange={(e) => setEmail(e.target.value)} 
                                className="profile-input" 
                            />
                        ) : (
                            <p className="profile-detail">{email}</p>
                        )}
                    </div>
                </div>
            </div>
            <div className="profile-actions">
                <button className="edit-button" onClick={handleEditClick}>
                    {isEditing ? 'Cancel' : 'Edit Profile'}
                </button>
                {isEditing && (
                    <button className="save-button" onClick={handleSaveChanges}>
                        Save Changes
                    </button>
                )}
            </div>
        </div>
    );
};

export default UserProfilePage;
