import React, { useEffect, useState } from 'react';
import { fetchUserDetails, updateUserDetails } from '/home/rguktongole/Desktop/ideanexus/frontend/src/services/userservice.js';
import '/home/rguktongole/Desktop/ideanexus/frontend/src/styles/profilepage.css';

const ProfilePage = ({ userId }) => {
  const [userDetails, setUserDetails] = useState({ name: '', email: '', bio: '' });
  const [editMode, setEditMode] = useState(false);
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        if (!userId) {
          setError('User ID is missing. Please log in again.');
          return;
        }

        const data = await fetchUserDetails(userId);
        setUserDetails(data);
        setError(''); // Clear any previous errors
      } catch (err) {
        console.error('Error fetching user details:', err);
        setError(err.response?.data?.message || 'Failed to fetch user details.');
      }
    };

    fetchData();
  }, [userId]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserDetails((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(''); // Clear previous errors
    setSuccessMessage(''); // Clear previous success messages

    try {
      const updatedData = await updateUserDetails(userDetails);

      // Ensure the updated user data is reflected
      setUserDetails(updatedData.user || userDetails);
      setSuccessMessage(updatedData.message || 'Profile updated successfully.');
      setEditMode(false);
    } catch (err) {
      console.error('Error updating profile:', err);
      setError(err.response?.data?.message || 'Failed to update profile.');
    }
  };

  return (
    <div className="profile-container">
      <h1>User Profile</h1>
      {error && <p className="error-message">{error}</p>}
      {successMessage && <p className="success-message">{successMessage}</p>}

      {!editMode ? (
        <div>
          <p><strong>Name:</strong> {userDetails.name || 'N/A'}</p>
          <p><strong>Email:</strong> {userDetails.email || 'N/A'}</p>
          <p><strong>Bio:</strong> {userDetails.bio || 'N/A'}</p>
          <button onClick={() => setEditMode(true)}>Edit Profile</button>
        </div>
      ) : (
        <form onSubmit={handleSubmit}>
          <label>
            Name:
            <input
              name="name"
              value={userDetails.name}
              onChange={handleInputChange}
              placeholder="Name"
              required
            />
          </label>
          <label>
            Email:
            <input
              name="email"
              type="email"
              value={userDetails.email}
              onChange={handleInputChange}
              placeholder="Email"
              required
            />
          </label>
          <label>
            Bio:
            <textarea
              name="bio"
              value={userDetails.bio}
              onChange={handleInputChange}
              placeholder="Bio"
              rows="4"
            ></textarea>
          </label>
          <div className="button-group">
            <button type="submit">Save</button>
            <button type="button" onClick={() => setEditMode(false)}>Cancel</button>
          </div>
        </form>
      )}
    </div>
  );
};

export default ProfilePage;
